import * as utils from "../../../Utils";

import { connect } from "react-redux";

const PostContainer = ({ name, post, setToken, history, getUser }) => {
  const deletePost = async () => {
    try {
      let response = await fetch(`${utils.backendRoute}/posts/del/${post}`, {
        method: "DELETE",
        headers: {
          jwt: setToken,
        },
      });
      await response.json();
      history.push("/home");
    } catch (err) {
      console.log(err);
    }
  };
  if (name === getUser) {
    return (
      <div className="column is-one-third" style={{ marginTop: "-20px" }}>
        <figure className="image is-1x1">
          <div className="x-logo delete is-medium" onClick={deletePost} />
          <img src={`${utils.imageRoute}/${post}`} alt="Placeholder" />
        </figure>
      </div>
    );
  } else {
    return (
      <div className="column is-one-third">
        <figure className="image is-1x1">
          <img src={`${utils.imageRoute}/${post}`} alt="Placeholder" />
        </figure>
      </div>
    );
  }
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps)(PostContainer);
