import * as utils from "../Utils";

export const getAllPosts = async (token) => {
  try {
    let response = await fetch(`${utils.backendRoute}/posts/getAll`, {
      method: "GET",
      headers: {
        jwt: token,
      },
    });
    let results = await response.json();
    return results.data.posts;
  } catch (err) {
    console.log(err);
  }
};
