import { useState } from "react";

import LoginView from "./LoginView";

import { connect } from "react-redux";
import { isLoggedIn, loginToken, setUser } from "../../actions";

import * as utils from "../../Utils";

const LoginContainer = ({ setUser, loginToken, isLoggedIn }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("Something went wrong....");

  const mySubmitHandler = async (event) => {
    event.preventDefault();
    const user = {
      username: username,
      password: password,
    };
    console.log("user here");
    console.log(user);

    try {
      let response = await fetch(`${utils.backendRoute}/auth/login`, {
        method: "POST",
        body: JSON.stringify(user),
        headers: {
          "Content-Type": "application/json",
        },
      });
      let result = await response.json();

      if (result.success) {
        setUser(user.username);
        loginToken(result.data.token);
        isLoggedIn(true);
      } else {
        setErrorMessage(result.msg);
        setIsError(true);
      }
    } catch (err) {
      setIsError(true);
    }
  };
  return (
    <>
      <LoginView
        setUsername={setUsername}
        setPassword={setPassword}
        mySubmitHandler={mySubmitHandler}
        isError={isError}
        errorMessage={errorMessage}
      />
    </>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, {
  isLoggedIn,
  loginToken,
  setUser,
})(LoginContainer);
